mapper.select:
SELECT YEAR (users_created_at) AS rowCreatedAt, COUNT (*) columnCount
FROM users
GROUP BY YEAR (users_created_at);

SELECT MONTH (users_created_at) AS rowCreatedAt, COUNT (*) columnCount
FROM users
GROUP BY MONTH (users_created_at);

SELECT DAY (users_created_at) AS rowCreatedAt, COUNT (*) columnCount
FROM users
GROUP BY DAY (users_created_at);
