mapper.select:
SELECT *
FROM users
WHERE users_email = #{usersEmail} AND users_pwd = #{usersPwd}

SELECT *
FROM users
WHERE users_email = #{usersEmail} AND users_name =#{usersName}

mapper.update:
UPDATE users
SET users_pwd = #{usersPwd}
WHERE users_email = #{usersEmail} AND users_name =#{usersName};
