mapper.select:
SELECT store_name, store_category, store_avgstar, store_mainimage FROM store
SELECT store_id as storeId, store_name as storeName, store_address as storeAddress, store_latitude as storeLatitude, store_longitude as storeLongitude, store_avgstar as storeAvgstar, store_intro as storeIntro, store_mainimage as storeMainimage, store_category as storeCategory, store_capacity as storeCapacity, store_tel as storeTel FROM store WHERE store_id = #{storeId}
SELECT store_name, store_category, store_avgstar, store_mainimage FROM store WHERE store_category = #{category}
