mapper.select:
SELECT COUNT(*)
FROM store
WHERE store_name = #{storeName} 
AND store_address = #{storeAddress}

SELECT store_id, store_name, store_address, store_latitude, store_longitude, store_avgstar, store_intro, store_mainimage, store_category, store_capacity, store_tel FROM store
AND store_category LIKE CONCAT(#{category}, '%')
ORDER BY store_avgstar DESC, store_id DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT store_id, store_name, store_address, store_latitude, store_longitude, store_avgstar, store_intro, store_mainimage, store_category, store_capacity, store_tel FROM store
AND ( store_name LIKE CONCAT('%', #{keyword}, '%') OR store_address LIKE CONCAT('%', #{keyword}, '%') OR store_category LIKE CONCAT('%', #{keyword}, '%') OR store_intro LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY CASE WHEN store_name LIKE CONCAT(#{keyword}, '%') THEN 1 WHEN store_name LIKE CONCAT('%', #{keyword}, '%') THEN 2 WHEN store_category LIKE CONCAT(#{keyword}, '%') THEN 3 WHEN store_address LIKE CONCAT('%', #{keyword}, '%') THEN 4 ELSE 5 END, store_avgstar DESC, store_id DESC LIMIT #{pageSize} OFFSET #{offset}
